-- Dieses SQL-Skript kann in HeidiSQL als Abfrage ausgeführt werden, um Testdaten für FindlunchV3 zu generieren.

USE findlunch;

DROP PROCEDURE IF EXISTS insert_test_values;
DELIMITER #
CREATE PROCEDURE IF NOT EXISTS insert_test_values(restaurant_number INT, offer_number INT, user_number INT)
BEGIN
DECLARE v1 INT DEFAULT 1;
DECLARE v2 INT DEFAULT 1;
DECLARE v3 INT DEFAULT 1;
DECLARE v4 INT DEFAULT 1;

/* Alte Daten löschen */
DELETE FROM reservation_offers;
DELETE FROM reservation;
DELETE FROM points;
DELETE FROM reset_password;
DELETE FROM favorites;
DELETE FROM user_pushtoken;
DELETE FROM `user`;
DELETE FROM reservation;
DELETE FROM offer_has_additives;
DELETE FROM offer_has_allergenic;
DELETE FROM offer_has_day_of_week;
DELETE FROM offer;
DELETE FROM course_types;
DELETE FROM opening_time;
DELETE FROM time_schedule;
DELETE FROM restaurant_has_kitchen_type;
DELETE FROM restaurant_logo;
DELETE FROM donation_per_month;
DELETE FROM restaurant;

/* ID soll wieder bei 1 beginnen. */
ALTER TABLE `user` AUTO_INCREMENT = 1;
ALTER TABLE offer AUTO_INCREMENT = 1;

WHILE v1 <= restaurant_number DO
/* Restaurant anlegen, aktuell mit identischem QR-Code */
INSERT IGNORE INTO findlunch.restaurant (`id`, `customer_id`, `name`, `street`, `street_number`, `zip`, `city`, `country_code`, `location_latitude`, `location_longitude`, `email`, `phone`, `url`, `restaurant_type_id`, `restaurant_uuid`, `qr_uuid`, `swa_offer_modify_permission`, `swa_blocked`, `swa_sales_person_id`) VALUES
(v1, v1+1000, CONCAT('Restaurant ',v1), 'Lothstraße', v1, '80335', 'München', 'DE', 48.155+(0.0001*v1),  11.555+(0.0001*v1), CONCAT('restaurant',v1,'@mail.de'), CONCAT('089/6666',v1), 'https://www.google.com', 3, CONCAT('5b36b3e3-054d-41b9-be3c-19fcece09fb5', '-', v1), _binary , 0, 0, 1);
/* Küchentyp angeben */
INSERT IGNORE INTO findlunch.restaurant_has_kitchen_type (`restaurant_id`, `kitchen_type_id`) VALUES (v1, 3), (v1, 4), (v1, 5);
/* Restaurantlogo anlegen */
INSERT IGNORE INTO findlunch.restaurant_logo (`restaurant_id`, `logo`, `thumbnail`) VALUES
	(v1, , );

/* Gänge festlegen */
WHILE v2 < 4 DO
INSERT IGNORE INTO findlunch.course_types (`restaurant_id`, `name`, `sort_by`) VALUES
(v1, CONCAT(v2, '. Gang'), v2);
SET v2 = v2 + 1;
END WHILE;
SET v2 = 1;

/* Angebotszeiten (Mo - So, 9 - 22 Uhr) und Öffnungszeiten (Mo - So, 8 - 23 Uhr) einfügen */
WHILE v2 < 7 DO
INSERT IGNORE INTO findlunch.time_schedule (`restaurant_id`, `offer_start_time`, `offer_end_time`, `day_of_week_id`) VALUES
(v1, '1970-01-01 09:00:00', '1970-01-01 22:00:00', v2);
INSERT IGNORE INTO findlunch.opening_time (`opening_time`, `closing_time`, `time_schedule_id`) VALUES ('1970-01-01 08:00:00', '1970-01-01 23:00:00', LAST_INSERT_ID());
SET v2 = v2 + 1;
END WHILE;
SET v2 = 1;

/* Angebote anlegen */
WHILE v2 <= (offer_number-1) DO
INSERT IGNORE INTO findlunch.offer (`restaurant_id`, `title`, `description`, `price`, `preparation_time`, `start_date`, `end_date`, `needed_points`, `sold_out`, `course_type`, `sort`) VALUES
(v1, CONCAT('Leberkässemmel (', v2, ' Scheibe)'), CONCAT('Knusprige warme Leberkässemmel mit ', v2, ' Scheiben frischem Leberkäse.'), CAST(v2 AS DOUBLE), 2, '2018-01-01', '2018-08-31', v2*5, 0, (SELECT id FROM findlunch.course_types WHERE restaurant_id = v1 ORDER BY RAND() LIMIT 1), v2);
/* Offer-ID speichern */
SET v3 = LAST_INSERT_ID();
/* Photo fehlt */
/* Allergene hinzufügen */
INSERT IGNORE INTO findlunch.offer_has_allergenic (`offer_id`, `allergenic_id`) VALUES
	(v3, FLOOR(1 + (RAND() * 10)));
/* Zusätze hinzufügen */
INSERT IGNORE INTO findlunch.offer_has_additives (`offer_id`, `additives_id`) VALUES
	(v3, FLOOR(1 + (RAND() * 10)));

/* Angebotstage hinzufügen */
WHILE v4 < 7 DO
INSERT IGNORE INTO findlunch.offer_has_day_of_week (`offer_id`, `day_of_week_id`) VALUES(v3, v4);
SET v4 = v4 + 1;
END WHILE;
SET v4 = 1;
SET v2 = v2 + 1;
END WHILE;

INSERT IGNORE INTO findlunch.offer (`restaurant_id`, `title`, `description`, `price`, `preparation_time`, `start_date`, `end_date`, `needed_points`, `sold_out`, `course_type`, `sort`) VALUES
	(v1, CONCAT('Leberkässemmel (', v2, ' Scheibe)'), CONCAT('Knusprige warme Leberkässemmel mit ', v2, ' Scheiben frischem Leberkäse.'), CAST(v2 AS DOUBLE), 2, '2018-01-01', '2018-08-31', v2*5, 1, (SELECT id FROM findlunch.course_types WHERE restaurant_id = v1 ORDER BY RAND() LIMIT 1), v2);

SET v2 = 1;
/* Benutzer für Anbieter mit Passwort '12345' hinzufügen */
INSERT IGNORE INTO findlunch.`user` (`username`, `password`, `restaurant_id`, `user_type_id`, `account_id`, `push_notification_enabled`) VALUES
	(CONCAT('restaurant',v1,'@mail.de'), '$2a$10$IDuTFwOGL4lFhzFG77UAB.oTVbtlvp35KeU835ihaVmR8SNdCEQx.', v1, 1, NULL, 0);
SET v1 = v1 +1;
END WHILE;
SET v1 = 1;

/* Restaurant 3 hat am Donnerstag nicht geöffnet. */
DELETE FROM findlunch.opening_time
WHERE time_schedule_id IN (
	SELECT DISTINCT id FROM findlunch.time_schedule
	WHERE restaurant_id = 3 AND day_of_week_id = 4);

DELETE FROM findlunch.time_schedule
WHERE restaurant_id = 3 AND day_of_week_id = 4;

/* Userbezogene Daten einfügen */
WHILE v1 <= user_number DO
/* Benutzer (Kunde) mit Passwort 12345 anlegen */
INSERT IGNORE INTO findlunch.`user` (`username`, `password`, `restaurant_id`, `user_type_id`, `account_id`, `push_notification_enabled`) VALUES
(CONCAT('kunde',v1,'@mail.de'), '$2a$10$IDuTFwOGL4lFhzFG77UAB.oTVbtlvp35KeU835ihaVmR8SNdCEQx.', NULL, 2, NULL, 0);

SET v2 = LAST_INSERT_ID();

/* Erstes Resteaurant als Favorit hinterlegen. */
INSERT IGNORE INTO findlunch.`favorites` (`user_id`, `restaurant_id`) VALUES(v2, 1);

/* Ersten zwei Restaurants mit Punkten hinterlegen. */
INSERT IGNORE INTO findlunch.`points` (`user_id`, `restaurant_id`, points) VALUES
	(v2, 1, 30),
	(v2, 2, 20);

SET v1 = v1 +1;
END WHILE;

/* Zwei Bestellungen einfügen */
INSERT INTO `findlunch`.`reservation` (`reservation_number`, `total_price`, `donation`, `used_points`, `points_collected`, `user_id`, `euro_per_point_id`, `restaurant_id`, `collect_time`, `timestamp_received`, `timestamp_responded`, `reservation_status_id`, `used_paypal`, `fee`, `pp_transaction_finished`) VALUES
	('500500500', '10', '0', '0', '0', '26', '1', '6', NOW(), NOW(), NOW(), '1', '0', '0', '0');
INSERT INTO `findlunch`.`reservation` (`reservation_number`, `total_price`, `donation`, `used_points`, `points_collected`, `user_id`, `euro_per_point_id`, `restaurant_id`, `collect_time`, `timestamp_received`, `timestamp_responded`, `reservation_status_id`, `used_paypal`, `fee`, `pp_transaction_finished`) VALUES
	('500500501', '10', '0', '0', '0', '27', '1', '7', NOW(), NOW(), NOW(), '1', '0', '0', '0');

/* Token für Reset einfügen */
INSERT INTO findlunch.`reset_password` (`user_id`, `token`, `date`) VALUES
	(28, 'abcdefg-e5f7-49b1-8d4e-4ecad9fce56a-28', NOW());
INSERT INTO findlunch.`reset_password` (`user_id`, `token`, `date`) VALUES
	(29, 'abcdefg-e5f7-49b1-8d4e-4ecad9fce56a-29', NOW());
INSERT INTO findlunch.`reset_password` (`user_id`, `token`, `date`) VALUES
	(30, 'abcdefg-e5f7-49b1-8d4e-4ecad9fce56a-30', NOW());
END
#
DELIMITER ;

-- Prozedur ausführen. Es werden 20 Restaurants mit jew. 10 Offers angelegt. Zusätzlich 10 Kunden.
CALL insert_test_values(20, 10, 10);